mapper.delete:
delete from tb_device
where device_id in

    #{id,jdbcType=VARCHAR}

mapper.select:
SELECT IFNULL(o.total_money,0) total_money,IFNULL(o.today_money,0) today_money, t.device_id, t.device_name, t.device_address, t.device_status, t.cargo_lane_1, t.cargo_lane_2, t.cargo_lane_3, t.cargo_lane_4, t.cargo_lane_5, t.cargo_lane_6, t.cargo_lane_7, t.cargo_lane_8, t.cargo_lane_9, t.cargo_lane_10, t.cargo_lane_11, t.cargo_lane_12, t.cargo_lane_13, t.cargo_lane_14, t.cargo_lane_15, t.cargo_lane_16, t.cargo_lane_17, t.cargo_lane_18, t.cargo_lane_19, t.cargo_lane_20, t.create_time, s.username update_user FROM tb_device t LEFT JOIN sys_user s ON t.update_user_id = s.id LEFT JOIN ( SELECT device_id, IFNULL(sum(total_price), 0) total_money, IFNULL( ( SELECT sum(total_price) FROM tb_order WHERE to_days(create_time) = TO_DAYS(now()) AND device_id = tb.device_id ), 0 ) today_money FROM tb_order tb GROUP BY tb.device_id ) o ON t.device_id = o.device_id
and s.username LIKE #{user_name}
and t.update_time >= #{begindate,jdbcType=DATE}
and t.update_time <= #{enddate,jdbcType=DATE}
and t.device_status = ${device_status}
GROUP BY t.device_id order by create_time desc
