mapper.delete:
delete from tb_order
where order_id in

    #{id,jdbcType=VARCHAR}

mapper.select:
select
t.order_id,t.device_id,t.cargo_lane,t.user_id,t.create_time,t.order_status,s.username user_name,d.device_name
,d.device_address
from tb_order t
left join sys_user s on t.user_id = s.id
left join tb_device d on t.device_id = d.device_id



        and s.username LIKE #{user_name}


          and  t.send_time >= #{begindate,jdbcType=DATE}


          and t.send_time <= #{enddate,jdbcType=DATE}


       and t.order_status = ${order_status}



order by  create_time desc
